import static org.hamcrest.Matchers.hasItems;
import static org.hamcrest.Matchers.is;
import static org.junit.Assert.assertThat;
import static org.springframework.security.test.web.servlet.setup.SecurityMockMvcConfigurers.springSecurity;

/**
* DB接続を伴うクラスのテストadviceクラス。
* @since 1.0.3
*/
@RunWith(value = SpringRunner.class)
@SpringBootTest
@DbUnitConfiguration(dataSetLoader = TestCsvDataSetLoader.class)
@TestExecutionListeners({
DependencyInjectionTestExecutionListener.class,
DirtiesContextTestExecutionListener.class,
TransactionDbUnitTestExecutionListener.class,
WithSecurityContextTestExecutionListener.class,
MockitoTestExecutionListener.class})
@Transactional
public abstract class DBTestSupport {
/** Mockitoを使用するための定義。@RunWithで別クラスが設定されているのでここで定義。 */
@Rule
public final MockitoRule rule = MockitoJUnit.rule();

/** テスト用コンテキスト. */
@Autowired
private WebApplicationContext context;

/** テスト用DIコンテナを用いたモック. */
protected MockMvc mockMvc;

/**
* モックをビルドする。
*/
@Before
public void setUpMockMvc() {
MockitoAnnotations.initMocks(this);
mockMvc = MockMvcBuilders
.webAppContextSetup(context)
.apply(springSecurity())
.build();
}

/**
* アサートの検証モード。
* @since 1.0.3
*/
public enum AssertionMode {
/** 順序も含めて厳密にアサートする。 */
DEFAULT,
/** 順序をアサートしない。 */
NON_STRICT_UNORDERED
}

/**
* CSVの期待値データとassertする。順序を含めて厳密に比較する。
* &lt;ul&gt; 期待値データ制約
* &lt;li&gt;同ディレクトリにtable-ordering.txtを作成し、テーブル名称を1行づつ列挙すること&lt;/li&gt;
* &lt;li&gt;ファイル名は、テーブル名称.csvとすること&lt;/li&gt;
* &lt;li&gt;1行目は、カラム物理名とすること&lt;/li&gt;
* &lt;li&gt;BOM無しUTF-8で保存されていること&lt;/li&gt;
* &lt;li&gt;nullデータを表す場合は、nullと記載すること&lt;/li&gt;
* &lt;/ul&gt;
* @param actual 実際のデータ。単一オブジェクト、もしくは、Listデータ。
* @param pathName 期待値csvの格納パス
* @param expectedFileName 期待値csvのファイル名
* @throws Exception 例外
*/
public void assertCsv(Object actual, String pathName, String expectedFileName) throws Exception {
assertCsv(actual, pathName, expectedFileName, AssertionMode.DEFAULT);
}

/**
* CSVの期待値データとassertする。
* &lt;ul&gt; 期待値データ制約
* &lt;li&gt;同ディレクトリにtable-ordering.txtを作成し、テーブル名称を1行づつ列挙すること&lt;/li&gt;
* &lt;li&gt;ファイル名は、テーブル名称.csvとすること&lt;/li&gt;
* &lt;li&gt;1行目は、カラム物理名とすること&lt;/li&gt;
* &lt;li&gt;BOM無しUTF-8で保存されていること&lt;/li&gt;
* &lt;li&gt;nullデータを表す場合は、nullと記載すること&lt;/li&gt;
* &lt;/ul&gt;
* @param actual 実際のデータ。単一オブジェクト、もしくは、Listデータ。
* @param pathName 期待値csvの格納パス
* @param expectedFileName 期待値csvのファイル名
* @param assertionMode アサートの検証モード
* @throws Exception 例外
*/
public void assertCsv(Object actual, String pathName, String expectedFileName, AssertionMode assertionMode) throws Exception {
List actualList;
if (actual instanceof List) {
actualList = (List) actual;
} else {
actualList = new ArrayList&lt;&gt;(1);
actualList.add(actual);
}

ITable expectedTable = new CsvDataSet(new File(DBTestSupport.class.getClassLoader().getResource(pathName.replaceFirst("^/", "")).getPath())).getTable(expectedFileName);
assertThat("Row count is not match.", actualList.size(), is(expectedTable.getRowCount()));

if (assertionMode == null || Objects.equals(AssertionMode.DEFAULT, assertionMode)) {
assertCsvDefault(actualList, expectedTable);
} else if (Objects.equals(AssertionMode.NON_STRICT_UNORDERED, assertionMode)) {
assertCsvNonStrictUnordered(actualList, expectedTable);
}
}

/**
* CSVからテストデータオブジェクトを作成する。
* &lt;ul&gt; CSVデータ制約
* &lt;li&gt;同ディレクトリにtable-ordering.txtを作成し、ファイル名を1行づつ列挙すること&lt;/li&gt;
* &lt;li&gt;1行目は、プロパティ名とすること&lt;/li&gt;
* &lt;li&gt;BOM無しUTF-8で保存されていること&lt;/li&gt;
* &lt;li&gt;nullデータを表す場合は、nullと記載すること&lt;/li&gt;
* &lt;/ul&gt;
* @param &lt;T&gt; T テストデータのオブジェクトの型
* @param targetClass テストデータのオブジェクトクラス
* @param pathName csvの格納パス
* @param expectedFileName csvのファイル名
* @return テストデータリスト
* @throws Exception 例外
*/
public &lt;T&gt; List&lt;T&gt; createTestDataFromCsv(Class&lt;T&gt; targetClass, String pathName, String expectedFileName) throws Exception {
ITable expectedTable = new CsvDataSet(new File(DBTestSupport.class.getClassLoader().getResource(pathName.replaceFirst("^/", "")).getPath())).getTable(expectedFileName);

List&lt;T&gt; targetList = new ArrayList&lt;&gt;(expectedTable.getRowCount());
for (int i = 0; i &lt; expectedTable.getRowCount(); i++) {
T target = targetClass.newInstance();
for (Column column : expectedTable.getTableMetaData().getColumns()) {
String columnName = column.getColumnName();
Object value = expectedTable.getValue(i, columnName);
if (value == null) {
continue;
}
String valueStr = value.toString();
Field field = targetClass.getField(columnName);

// 対象プロパティの型に合わせてsetする。
if (String.class.equals(field.getType())) {
field.set(target, valueStr);
} else if (Long.class.equals(field.getType())) {
field.set(target, Long.valueOf(valueStr));
} else if (Integer.class.equals(field.getType())) {
field.set(target, Integer.valueOf(valueStr));
} else if (Double.class.equals(field.getType())) {
field.set(target, Double.valueOf(valueStr));
} else if (Float.class.equals(field.getType())) {
field.set(target, Float.valueOf(valueStr));
} else if (Short.class.equals(field.getType())) {
field.set(target, Short.valueOf(valueStr));
} else if (Boolean.class.equals(field.getType())) {
field.set(target, Boolean.valueOf(valueStr));
} else if (Byte.class.equals(field.getType())) {
field.set(target, Byte.valueOf(valueStr));
} else if (LocalDateTime.class.equals(field.getType())) {
field.set(target, LocalDateTime.parse(valueStr));
} else {
field.set(target, valueStr);
}
}
targetList.add(target);
}
return targetList;
}

/**
* CSVの期待値データとassertする。順序を含めて厳密に比較する。
* @param actualList 実データリスト
* @param expectedTable 期待値CSVデータ
* @throws Exception 例外
*/
private void assertCsvDefault(List actualList, ITable expectedTable) throws Exception {
for (int i = 0; i &lt; expectedTable.getRowCount(); i++) {
for (Column column : expectedTable.getTableMetaData().getColumns()) {
String columnName = column.getColumnName();
// 各行、各列に対して一つづつ厳密にアサートする。
assertThat("{columnName-&gt;" + columnName + ", rowNumber-&gt;" + i + "} is not match.",
toSafeString(getPropertyValue(actualList.get(i), toCamelCase(columnName))),
is(toSafeString(expectedTable.getValue(i, columnName))));
}
}
}

/**
* CSVの期待値データとassertする。順序は比較しない。
* @param actualList 実データリスト
* @param expectedTable 期待値CSVデータ
* @throws Exception 例外
*/
private void assertCsvNonStrictUnordered(List actualList, ITable expectedTable) throws Exception {
// Beanの各プロパティを文字列結合して比較する。
List&lt;String&gt; convertedActualList = new ArrayList&lt;&gt;();
List&lt;String&gt; convertedExpectedList = new ArrayList&lt;&gt;();
for (int i = 0; i &lt; expectedTable.getRowCount(); i++) {
StringBuilder convertedActual = new StringBuilder();
StringBuilder convertedExpected = new StringBuilder();
for (Column column : expectedTable.getTableMetaData().getColumns()) {
String columnName = column.getColumnName();
convertedActual.append(toSafeString(getPropertyValue(actualList.get(i), toCamelCase(columnName)))).append("\t");
convertedExpected.append(toSafeString(expectedTable.getValue(i, columnName))).append("\t");
}
convertedActualList.add(convertedActual.toString());
convertedExpectedList.add(convertedExpected.toString());
}
// 順不同のアサートとするためにhasItemsを用いる。両方向のアサートをすることで、行の重複にも対応。
assertThat(convertedActualList, hasItems(convertedExpectedList.toArray(new String[0])));
assertThat(convertedExpectedList, hasItems(convertedActualList.toArray(new String[0])));
}

/**
* スネークケースからキャメルケースへ変換する。
* @param snakeCaseStr スネークケース文字列
* @return キャメルケース文字列
*/
private String toCamelCase(String snakeCaseStr) {
if (Dx3StringUtils.isBlank(snakeCaseStr) || !snakeCaseStr.contains("_")) {
return snakeCaseStr;
}
StringBuilder sb = new StringBuilder();
char[] chars = snakeCaseStr.toCharArray();
sb.append(Character.toLowerCase(chars[0]));
for (int i = 1; i &lt; chars.length; i++) {
char c = chars[i];
if (c == '_') {
sb.append(Character.toUpperCase(chars[++i]));
} else {
sb.append(Character.toLowerCase(chars[i]));
}
}
return sb.toString();
}

/**
* オブジェクトからフィールド値を取得する。
* @param object 対象オブジェクト
* @param fieldName フィールド名
* @return フィールド値
* @throws NoSuchFieldException 想定外の例外
* @throws IllegalAccessException 想定外の例外
*/
private Object getPropertyValue(Object object, String fieldName) throws NoSuchFieldException, IllegalAccessException {
Field field = object.getClass().getField(fieldName);
field.setAccessible(true);
return field.get(object);
}

/**
* {@link NullPointerException}を起こさないようにtoStringする。
* @param nullableStr nullかもしれない文字列
* @return toString結果。元がnullならばnullを返却する。
*/
private String toSafeString(Object nullableStr) {
return nullableStr == null ? null : nullableStr.toString();
}
}

